const cp = require('.')



const staticShapes = new cp.BBTree(null)
const activeShapes = new cp.BBTree(staticShapes)


//console.log(staticShapes)
{
  const circ = new cp.CircleShape(null, 10, cp.v(0,0))
  circ.update(cp.v(0,0), cp.v(1,0))

  staticShapes.insert(circ)
//staticShapes.query(cp.bb(-1000, -1000, 1000, 1000), (x) => console.log(x))
}

{
  const circ = new cp.CircleShape(null, 10, cp.v(0,0))
  circ.update(cp.v(20,20), cp.v(1,0))

  activeShapes.insert(circ)
//staticShapes.query(cp.bb(-1000, -1000, 1000, 1000), (x) => console.log(x))
}

activeShapes.collideStatic(staticShapes, (a, b) => {
  console.log(a, b)
})

